import {
  Strategy as BaseStrategy,
  StrategyOptions,
  VerifyFunction,
  StrategyOptionsWithRequest,
  VerifyFunctionWithRequest,
} from 'passport-oauth2';

declare module 'passport-naver-v2' {
  export type Profile = {
    provider: 'naver';
    id: string;
    nickname?: string;
    profileImage?: string;
    age?: string;
    gender?: string;
    email?: string;
    mobile?: string;
    mobileE164?: string;
    name?: string;
    birthday?: string;
    birthYear?: string;
    _raw: string;
    _json: string;
  };
  
  export class Strategy extends BaseStrategy {
    constructor(options: Partial<StrategyOptions>, verify: VerifyFunction);
    constructor(options: Partial<StrategyOptionsWithRequest>, verify: VerifyFunctionWithRequest);

    userProfile(accessToken: string, done: (error: Error, profile?: Profile) => void): void;
    authorizationParams(options: any): any;

    name: 'naver';
  }
}
